import React, { useState, useEffect } from "react";
import Landing from "./landing/Landing.js";
import Projects from "./projects/Projects.js";
import Footer from "./Footer.js";
import { Button } from "react-bootstrap";
import { BiUpArrow } from "@react-icons/all-files/bi/BiUpArrow";
import { BiDownArrow } from "@react-icons/all-files/bi/BiDownArrow";
import ContactMe from "./contactme/ContactMe.js";
import Aos from "aos";
import "aos/dist/aos.css";

function Home() {
  useEffect(() => {
    Aos.init({ duration: 1500 });
  }, []);
  const unSelected = "my-1 fc-white nav-btn-size rounded-circle bg-secondary";
  const selected = "my-1 text-dark nav-btn-size rounded-circle bg-white";

  const [btn1, setLanding] = useState("1");
  const [btn2, setProjects] = useState("2");
  const [btn3, setResume] = useState("3");

  const [btn1C, setLandingC] = useState(unSelected);
  const [btn2C, setProjectsC] = useState(unSelected);
  const [btn3C, setResumeC] = useState(unSelected);

  const stageLanding = (label, style) => {
    setLanding(label);
    setLandingC(style);
  };
  const stageProjects = (label, style) => {
    setProjects(label);
    setProjectsC(style);
  };
  const stageResume = (label, style) => {
    setResume(label);
    setResumeC(style);
  };

  return (
    <div>
      <div className="sticky">
        {/* <div data-aos="fade-up">
          <BiUpArrow size="5.5vh" />
        </div> */}
        <Button
          className={btn1C}
          data-aos="fade-top"
          onMouseEnter={() => stageLanding("Landing", selected)}
          onMouseOut={() => stageLanding("1", unSelected)}
        >
          {btn1}
        </Button>
        <Button
          className={btn2C}
          data-aos="fade-top"
          onMouseEnter={() => stageProjects("Projects", selected)}
          onMouseOut={() => stageProjects("2", unSelected)}
        >
          {btn2}
        </Button>
        <Button
          className={btn3C}
          data-aos="fade-top"
          onMouseEnter={() => stageResume("Contact", selected)}
          onMouseOut={() => stageResume("3", unSelected)}
        >
          {btn3}
        </Button>
        {/* <div data-aos="fade-down">
          <BiDownArrow size="5.5vh" />
        </div> */}
      </div>
      <Landing />
      <Projects />
      <Footer />
      <ContactMe />
    </div>
  );
}

export default Home;
